class Solution():
    def charactersBeforeSOPDetected(self,text,max):
        start = 0
        unique = set(list(text[start:max]))
        while len(unique)<max:
            start+=1
            end+=1
            unique = set(list(text[start:max]))

        return end

    def main(self):
        question = ''
        test1 = 'bvwbjplbgvbhsrlpgdmjqwftvncz'
