        test3 = 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg'
        test4 = 'zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'

        print(self.charactersBeforeSOPDetected(question,4))

